vfnt ;convert pic from font 2048            call    readfile2n ;считаем первые 64 сектора            call    clearpag            ld      hl,loadadr2            ld      de,#c000+5120+8 ;в окно            ld      b,8  ;строкvfnt3            push    bc            push    de            ld      b,32 ;столбцовvfnt2            push    bc            push    de            ld      b,8  ;1 символvfnt1            ; ld      a,(hl)            ; ld      (de),a			call stretchPix ;удвоить пиксели по ширине			            push    hl            ld      hl,80-1            add     hl,de            ld      d,h            ld      e,l            pop     hl            inc     hl            djnz    vfnt1            pop     de            inc     de			inc     de            pop     bc            djnz    vfnt2            pop     de            push    hl            ld      hl,640            add     hl,de            ld      d,h            ld      e,l            pop     hl            pop     bc            djnz    vfnt3            ;коррекция числа страниц            ld      a,2 ; две            ld      (allpicpag),a ; всего страниц            call    showpic ;показать картинку            xor     a            call    pag_on            call    gmxpagoff            ret