vgmx0f ; pic from GMX 0f (16128)            call    vgmx0fp ;пиксели            ;теперь атрибуты            call    vgmx0fa            ;коррекция числа страниц            ld      a,2 ; две            ld      (allpicpag),a ; всего страниц            call   showpic ;показать картинку            xor     a            call    pag_on            call    gmxpagoff            retvgmx0fp ;pixel            call    clearpag			ld 		hl,#c000 ;заполняем страницу пикселей числом 0f			ld 		de,#c001			ld 		bc,16000			ld		(hl),#0f			ldir            retvgmx0fa ;convert atr             ;теперь загружаем атрибуты как есть            ld      a,(curpicpag)            inc     a            call    pag_on            call    readfile2n ;считаем первые 64 сектора или весь файл, если он меньше			ld 		hl,#8000+128 ;пропустим заголовок файла			ld 		de,#c000			ld 		bc,16000			ldir			            ret