vzxm12288mlt ;convert pic from ZX multiColor 12288mcx .mlt            call    vzxp ;пиксели            ;теперь атрибуты            call    vzxm12288mlta            ;коррекция числа страниц            ld      a,2 ; две            ld      (allpicpag),a ; всего страниц            call   showpic ;показать картинку            xor     a            call    pag_on            call    gmxpagoff            retvzxm12288mlta ;convert atr             ;теперь атрибуты            ld      a,(curpicpag)            inc     a            call    pag_on            ld      hl,loadadr2+6144 ;откуда            ld      de,#c000+320 ;левый верхний            ld      b,192  ;цикл строкvzxm12288mlta3            push    bc			ex 		de,hl            ld      bc,(shiftL)            add     hl,bc			ex 		de,hl            ;push    hl            ld      b,32  ;столбцовvzxm12288mlta1            ld      a,(hl)            ld      (de),a            inc     de            ld      (de),a            inc     hl            inc     de            djnz    vzxm12288mlta1 			ex 		de,hl            ld      bc,(shiftR)            add     hl,bc			ex 		de,hl            ;ld      bc,32            ;add     hl,bc            pop     bc            djnz    vzxm12288mlta3            ret