vzxm9216 ;convert pic from ZX multiColor 9216 .ifl            call    vzxp ;пиксели            ;теперь атрибуты            call    vzxm9216a            ;коррекция числа страниц            ld      a,2 ; две            ld      (allpicpag),a ; всего страниц            call   showpic ;показать картинку            xor     a            call    pag_on            call    gmxpagoff            retvzxm9216a ;convert atr             ;теперь атрибуты            ld      a,(curpicpag)            inc     a            call    pag_on            ld      hl,loadadr2+6144 ;откуда            ld      de,#c000+320 ;левый верхний            ld      b,192/2  ;цикл строкvzxm9216a3            push    bc			ld 		b,2 ;дублирование строки атрибутовvzxm9216a2			push	hl			push 	bc			ex 		de,hl            ld      bc,(shiftL)            add     hl,bc			ex 		de,hl            ;push    hl            ld      b,32  ;столбцовvzxm9216a1            ld      a,(hl)            ld      (de),a            inc     de            ld      (de),a            inc     hl            inc     de            djnz    vzxm9216a1		 			ex 		de,hl            ld      bc,(shiftR)            add     hl,bc			ex 		de,hl			pop		bc			ld		(TempHL),hl			pop		hl			djnz	vzxm9216a2			ld		hl,(TempHL)            ;ld      bc,32            ;add     hl,bc            pop     bc            djnz    vzxm9216a3            retTempHL		dw		0			